mapper.select:
		SELECT * FROM (SELECT ROWNUM RN, A.* 
            FROM (SELECT * FROM CBOARD ORDER BY BGROUP DESC, BSTEP) A)
        		WHERE RN BETWEEN #{startRow} AND #{endRow}

mapper.update:
		UPDATE CBOARD SET BNAME=#{bname},
                    BTITLE = #{btitle},
                    BCONTENT = #{bcontent},
                    BIP = #{bip}
        WHERE BID= #{bid}
