select.if:
SELECT * FROM (SELECT ROWNUM RN, A.*, (SELECT COUNT(*) FROM FCOMMENT WHERE FID=A.FID) commentCnt
        FROM (SELECT * FROM FBOARD ORDER BY FGROUP DESC, FSTEP) A)
    WHERE RN BETWEEN #{startRow} AND #{endRow}

mapper.update:
UPDATE FBOARD 
    SET FTITLE = #{ftitle},
        FFILE = #{ffile},
        FCONTENT = #{fcontent}
    WHERE FID = #{fid}
